mapper.select:
select
  order_no, order_type, create_time, order_amount, vip_time, remain_time, state
from fqgj.pay_order


        and order_no = #{order_no}


        and order_type = #{order_type}


        and create_time = #{create_time}


        and order_amount = #{order_amount}


        and vip_time = #{vip_time}


        and remain_time = #{remain_time}


        and state = #{state}
