mapper.select:
select
  order_no, order_type, create_time, order_amount, vip_time, remain_time, state, account_id,pay_type,user_phone
from fqgj.pay_order


        and order_no = #{order_no}


        and order_type = #{order_type}


        and create_time = #{create_time}


        and order_amount = #{order_amount}


        and vip_time = #{vip_time}


        and remain_time = #{remain_time}


        and state = #{state}


        and account_id = #{account_id}


        and pay_type = #{pay_type}


        and user_phone = #{user_phone}


        and date_format(create_time,'%y-%m-%d') >= date(#{start_time})


        and date_format(create_time,'%y-%m-%d') <= date(#{end_time})


order by create_time desc
